package in.string;

public class StringIndexOf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//indexOf()
		
		String s = "This is Roushan";
		
		System.out.println(s.indexOf('s'));   //return integer value
		
		System.out.println(s.lastIndexOf('s'));    //return integer value
	}
}
