package in.string;

public class StringStartWithEndWith {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s = "This is a java course";
		
		System.out.println(s.startsWith("Th"));   //return boolean value
		
		System.out.println(s.endsWith("se"));     //return boolean value
		
		System.out.println(s.contains("is"));     //return boolean value
		
	}

}
