package in.string;

public class StringSubsequence {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s = "Roushan";
		
		System.out.println(s.subSequence(3, 6));    //return charSequence
		
		System.out.println(s.substring(4));         //return string value
		
		System.out.println(s.substring(2,6));
	}

}
